import React, { useEffect, useState } from "react";
import {useSelector} from 'react-redux';
import "../Styles/EditProfile.css"
import { useNavigate } from "react-router-dom";
import ProfileService from "../Service/Service";

export default function EditProfile(){
  let navigate  = useNavigate();
  const user = useSelector((state) =>state.profile.value)

  const[input, setInput] = useState({
    fullName: `${user.fullName}`,
    phone: `${user.phone}`,
    email: `${user.email}`,
    gender: `${user.gender}`,
  },[user]);

  const handleChange = (event) =>{
    setInput({...input, [event.target.name]: event.target.value});
  }

  const submitHandle = async(event)=>{
    event.preventDefault()

    //profile
    const newProfile = {
      _id: user._id,
      fullName: input.fullName,
      phone: input.phone,
      email: input.email,
      gender: input.gender
    }

    await ProfileService.updateProfile(newProfile)

    // navigate('/main')
    window.location.reload();
  }



  return(
    <div className="formMain">
      {/*<Header />*/}
      <div className="container contact-form" style={{ marginTop: "70px" }}>
        <br/>
        <button type="button" className="btn btn-primary btn-rounded" onClick={() => { navigate(-1) }}>Go Back</button>
        <div className="contact-image" >
          <img src="https://image.ibb.co/kUagtU/rocket_contact.png" alt="rocket_contact"/>
        </div>
        <form onSubmit={submitHandle}>
          <h3>Edit Profile</h3>
          <div className="row">
            <div className="col align-self-center">
              <div className="form-group">
                <input type="text" name="fullName" className="form-control" placeholder="Full Name *" onChange={handleChange} value={input.fullName}/>
              </div>
              <div className="form-group">
                <input type="text" name="phone" className="form-control" placeholder="Phone *" onChange={handleChange} value={input.phone}/>
              </div>
              <div className="form-group">
                <input type="text" name="email" className="form-control" placeholder="Email *" onChange={handleChange} value={input.email}/>
              </div>
              <div className="form-group">
                <input type="text" name="gender" className="form-control" placeholder="Gender *" onChange={handleChange} value={input.gender}/>
              </div>
              <div className="form-group">
                <input type="submit" name="btnSubmit" className="btnContact" placeholder="Edit"/>
              </div>
            </div>
          </div>
        </form>
      </div>
    </div>
  )
}
