import React, {useEffect, useRef, useState} from "react";
import "../../Post/Styles/createPost.css";
import { BsFillTagFill, BsFillEmojiHeartEyesFill, BsPinMapFill, BsFillFileEarmarkImageFill} from "react-icons/bs";
import PostService from "../Service/service";

export default function CreatePost(){
  const description = useRef();
  const [file, setFile] = useState(null);
  const[data, setData] = useState({});

  const getValue = async ()=>{
    await PostService.getCurrentUser()
      .then((res) =>{
        // console.log(res.data)
        setData(res.data)
      })
      .catch((err)=>{
        console.log(err)
      })
  }

  useEffect(()=>{
    getValue()
  },[])

  const handleChange = (event) =>{
    setFile(event.target.files[0])
  }

  const submitHandler = async(e)=>{
    e.preventDefault()

    //post
    const newPost = {
      authorId: data._id,
      authorName: data.fullName,
      description: description.current.value,
      image: file
    }

    await PostService.createPost(newPost)

    window.location.reload();
  }

  return(
    <>
      <div className="container contact-form">
        <div className="contact-image">
          <img src="https://image.ibb.co/kUagtU/rocket_contact.png" alt="rocket_contact"/>
        </div>
        <form method="post">
          <h3>Drop Us a Message</h3>
          <div className="row">
            <div className="col-md-6">
              <div className="form-group">
                <input type="text" name="txtName" className="form-control" placeholder="Your Name *" value=""/>
              </div>
              <div className="form-group">
                <input type="text" name="txtEmail" className="form-control" placeholder="Your Email *" value=""/>
              </div>
              <div className="form-group">
                <input type="text" name="txtPhone" className="form-control" placeholder="Your Phone Number *" value=""/>
              </div>
              <div className="form-group">
                <input type="submit" name="btnSubmit" className="btnContact" value="Send Message"/>
              </div>
            </div>
            <div className="col-md-6">
              <div className="form-group">
                <textarea name="txtMsg" className="form-control" placeholder="Your Message *"
                          style="width: 100%; height: 150px;"></textarea>
              </div>
            </div>
          </div>
        </form>
      </div>
    </>
  )
}
