import React, {useEffect, useRef, useState} from "react";
import { io } from "socket.io-client";
import Contacts from "../Pages/Contacts";
import "../../../styles/chat.css"
import Header from "../../Header/Header";
import ChatContainer from "../Pages/ChatContainer";
import ChatService from "../Service/service";
import UserService from "../../Header/Service/service";
import ChatInput from "../Pages/ChatInput";
import { v4 as uuidv4 } from 'uuid';

const host = "http://localhost:4000";

export default function Chat(){
  const socket = useRef();
  const [contacts, setContacts] = useState([]);
  const [currentChat, setCurrentChat] = useState([]);
  const [currentUser, setCurrentUser] = useState([]);

  const getCurrentUser = async () =>{
    await UserService.getCurrentUser()
      .then((res) =>{
        setCurrentUser(res.data);
      })
      .catch((err)=>{
        console.log(err);
      })
  }

  useEffect(() =>{
    getCurrentUser();
  });

  useEffect(() =>{
    if (currentUser){
      socket.current = io.connect(host);
      socket.current.emit("add-user", currentUser._id);
    }
  },[])

  const getValue = async ()=>{
    await ChatService.getAllUser()
      .then((res) =>{
        setContacts(res.data);
      })
      .catch((err)=>{
        console.log(err);
      })
  }

  useEffect(()=>{
    getValue();
  },[])

  const handleChatChange = (chat) =>{
    setCurrentChat(chat);
  }

  //Chat container
  const [messages, setMessages] = useState([]);
  const [arrivalMessage, setArrivalMessage] = useState(null);
  // const scrollRef = useRef();

  const getValue1 = async ()=>{
    await ChatService.recieveMessage({
      from: currentUser._id,
      to: currentChat._id,
    })
      .then((res)=>{
        setMessages(res.data)
      })
  }

  useEffect(()=>{
    getValue1()
  },[currentChat])

  const handleSendMsg = async (msg) =>{
    await ChatService.sendMessage({
      from: currentUser._id,
      to: currentChat._id,
      content: msg,
    })

    const msgs = [...messages];
    msgs.push({ fromSelf: true, chat: msg });
    setMessages(msgs);

    socket.current.emit("send-msg", {
      to: currentChat._id,
      from: currentUser._id,
      msg,
    });
  }

  useEffect(() =>{
    if (socket.current){
      socket.current.on("msg-recieve", (msg)=>{
        setArrivalMessage({ fromSelf: false, content: msg })
      })
    }
  },[])

  useEffect(() => {
    arrivalMessage && setMessages((prev) => [...prev, arrivalMessage]);
  }, [arrivalMessage]);

  // useEffect(() => {
  //   scrollRef.current?.scrollIntoView({ behavior: "smooth" });
  // }, [messages]);

  return(
    <>
      <Header />

      <div className="container" style={{ marginTop: "80px" }}>
        <div className="row clearfix">
          <div className="col-lg-12">
            <div className="card chat-app">
              <Contacts users={contacts} changeChat={handleChatChange}/>
              {/*<ChatContainer*/}
              {/*  currentChat={currentChat} currentUser={currentUser} socket={{socket}}*/}
              {/*/>*/}

              <div className="chat">
                <div className="chat-header clearfix">
                  <div className="row">
                    <div className="col-lg-6">
                      <a href="" data-toggle="modal" data-target="#view_info">
                        <img src="https://bootdey.com/img/Content/avatar/avatar2.png" alt="avatar" />
                      </a>
                      <div className="chat-about">
                        <h6 className="m-b-0">{currentChat.fullName}</h6>
                        <small>Last seen: 2 hours ago</small>
                      </div>
                    </div>
                  </div>
                </div>
                <div className="chat-history" style={{ height:"500px" }}>
                  {
                    messages.map((item)=>(
                      <div ref={socket} key={uuidv4()}>
                        <ul className="m-b-0">
                          <li className="clearfix">
                            {/*<div className="message-data text-right">*/}
                            {/*  <span className="message-data-time">10:10 AM, Today</span>*/}
                            {/*  <img src="https://bootdey.com/img/Content/avatar/avatar7.png" alt="avatar" />*/}
                            {/*</div>*/}
                            <div className={`message other-message ${item.fromSelf ? "float-right" : ""}`}>
                              {item.chat}
                            </div>
                          </li>
                        </ul>
                      </div>
                    ))
                  }
                </div>
                <ChatInput handleSendMsg={handleSendMsg} />
              </div>

            </div>
          </div>
        </div>
      </div>
    </>
  )
}
