import { configureStore, combineReducers  } from '@reduxjs/toolkit';
import adminReducer from '../modules/Admin/adminSlice';

export const rootReducer = combineReducers({
    admin: adminReducer,
    
});

export const store = configureStore({
    reducer: rootReducer,
});
