import React, { useEffect, useState } from 'react';
import ProfileService from "../Service/Service";
import {useSelector} from 'react-redux';

export default function GlobalFriendBtn({classbtn, user, profile}){
  const [friend,setFriend] = useState(false);
  const [load, setLoad] = useState(false);

  useEffect(()=>{
    if(profile.following.find(item => item._id === user._id)){
        setFriend(true)
    }
  },[profile.following,user._id])

  const addfriend = () => {
    if(load) return;
    setFriend(true)
    setLoad(true)
    // dispatch(addfriends({users:profile.users, user, auth, socket}))
    setLoad(false)
    
}
const removefriend = () =>{
    if(load) return;
    setFriend(false)
    setLoad(true)
    // dispatch(unfriends({users:profile.users, user, auth, socket}))
    setLoad(false)
}

  return(
    <>
      {
            friend ? 
            <button type="button" className="profileinfo-statRight btn btn-primary">
              UnFriend
            </button>
            :<button type="button" className="profileinfo-statRight btn btn-primary">
              Add Friend
            </button>  
        }
    </>
  )
}
