import React, {useEffect, useState} from "react";
import {useSelector, useDispatch} from 'react-redux';
import "../../components/Notification/Styles/notification.css"
import Header from "../Header/Header";
import NotifyService from "../Notification/Service/service";

export default function Notification(){
  const [notify, setNotify] = useState([]);

  const getNotify = async ()=>{
    await NotifyService.getNotify()
      .then((res)=>{
        setNotify(res.data)
      })
      .catch((err)=>{
        console.log(err)
      })
  }

  useEffect(()=>{
    getNotify()
  },[])

  return(
    <>
      <Header />
      <div className="container justify-content-center d-flex flex-column-reverse" style={{ marginTop: "40px" }}>
        <div className="card mt-5 p-2" style={{ marginTop:"0px",marginBottom:"0px"}}>
          <h2>Notification</h2>
          {
            notify.length > 0 && notify?.map((item,index)=>{
              <div className="media mt-3">
                <img src="https://imgur.com/3UmlFEf.png" className="mr-3" alt="" />
                <div className="media-body">
                  <h6 className="mt-2 mb-0">You have received &dollar;</h6>
                  <small className="text">Mar 20,2020</small>
                </div>
              </div>

            })
          }
        </div>

      </div>
    </>
  )
}
