import React, {useEffect, useState} from "react";
import ProfileService from "../Service/Service";
import {useSelector} from 'react-redux';
import { useNavigate, useParams } from "react-router-dom";

export default function UpdateSingleUserPost(){
  let navigate  = useNavigate();
  const { id } = useParams();
  const posts = useSelector((state) =>state.post.value)
  const[data, setData] = useState({
    title: ``,
    description: ``,
  });

  useEffect(()=>{
    if (posts){
      const found = posts.find(item => item._id === id)

      console.log(found)
      setData({
        title: `${found.title}`,
        description: `${found.description}`,
      })

    }
  },[posts])


  return(
    <>
      <div className="formMain">
        {/*<Header />*/}
        <div className="container contact-form" style={{ marginTop: "70px" }}>
          <div className="contact-image" >
            <img src="https://image.ibb.co/kUagtU/rocket_contact.png" alt="rocket_contact"/>
          </div>
          <form>
            <h3>Edit Post</h3>
            <div className="row justify-content-center" >
              <div className="col-8">
                <div className="col-md-6">
                  <div className="form-group">
                    <input type="text" name="title" className="form-control"  style={{width: "400px"}} placeholder="Title *" />
                  </div>
                  <div className="form-group">
                    <textarea  id="description" name="description" className="form-control" placeholder="Content *"
                           style={{width: "400px", height: "150px"}} ></textarea>
                  </div>
                </div>
                <div className="form-group">
                  <input type="submit" name="btnSubmit" className="btnContact" style={{marginLeft:"15px"}} value="Edit Post"/>
                </div>
              </div>
            </div>
          </form>
        </div>
      </div>
    </>
  )
}
