import {Http} from "../../../api/http";

const API_ENDPOINT = {
  UPDATEPROFILE: "/editProfile",
}

class ProfileServices{
  updateProfile(data){
    return Http.post(API_ENDPOINT.UPDATEPROFILE, data)
  }
}

const ProfileService = new ProfileServices();
export default ProfileService;
