import React, {useState, useEffect } from "react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import { Bar } from "react-chartjs-2";
import HeaderAdmin from "./headerAdmin";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);



export default function ChartAdmin(){
  const [chartData, setChartData] = useState({
    datasets: [],
  });

  const [chartOptions, setChartOptions] = useState({});

  useEffect(() => {
    setChartData({
      labels: ["John", "Kevin", "Geroge", "Micheal", "Oreo"],
      datasets: [
        {
          label: "Xanh",
          data: [12, 55, 34, 120, 720],
          borderColor: "rgb(53, 162, 235)",
          backgroundColor: "rgba(53, 162, 235, 0.4)",
        },
        {
          label: "Do ",
          data: [2, 50, 30, 100, 600],
          borderColor: "rgb(53, 162, 235)",
          backgroundColor: "red",
        },
      ],
    });
    setChartOptions({
      responsive: true,
      plugins: {
        legend: {
          position: "right",
        },
        title: {
          display: true,
          text: "Whom'st let the dogs out",
        },
      },
    });
  }, []);

  return(
    <div className="container" style={{ width: 700 }}>
      <HeaderAdmin />
      <div className="container-fluid gedf-wrapper" style={{ marginTop: "100px" }}>
        <Bar options={chartOptions} data={chartData} />
      </div>
    </div>
  )
}
