import React, {useEffect, useState} from "react";
import "../Styles/SingleUserPosts.css"
import PostService from "../../../modules/Post/Service/service";
import {useSelector} from 'react-redux';

export default function SingleUserPosts({user}){
  const[data,setData] = useState([])

  const getSinglePost = async()=>{
    const UserId = user._id

    if(UserId){
      await PostService.getSinglePost(UserId)
        .then((res)=>{
          setData(res.data)
          console.log(res.data)
        })
    }
  }

  useEffect(()=>{
    getSinglePost()
  },[user])

  return(
    <>
      {
        data.map((item)=>(
          <div className="posts card">
            <div className="card-header">
              {item.title}.
            </div>
            <div className="card-body">
              {/*<h5 className="card-title">Special title treatment</h5>*/}
              <p className="card-text">{item.description}.</p>
              <a href={`/detailPost/${item._id}`} className="btn btn-primary">View Detail</a>
            </div>
          </div>
        ))
      }
    </>
  )
}
