import React, {useContext, useEffect, useState} from "react";
import UserService from "./Service/service";
import { BsFillChatDotsFill, BsBell, BsPersonCircle, BsFillBookFill } from "react-icons/bs";
import Chat from "../Chat/Components/Chat";
import "./Styles/header.css"
import { useSelector } from "react-redux";
import NotifyService from "../Notification/Service/service";

export default function Header(){
  const[data, setData] = useState({});
  const [notify, setNotify] = useState([]);
  const [open, setOpen] = useState(false);

  const handleLogOut = async () =>{
    localStorage.removeItem('token')
  }

  const getValue = async ()=>{
    await UserService.getCurrentUser()
      .then((res) =>{
        // console.log(res.data)
        setData(res.data)
      })
      .catch((err)=>{
        console.log(err)
      })
  }

  useEffect(()=>{
    getValue()
  },[])

  const getNotify = async ()=>{
    await NotifyService.getNotify()
      .then((res)=>{
        setNotify(res.data)
      })
      .catch((err)=>{
        console.log(err)
      })
  }

  useEffect(()=>{
    getNotify()
  },[])
  return(
    <>
    <nav>
      <div className="logo"> BBBOOTSTRAP.COM</div>
      <div className="icon" id="bell"><img src="https://i.imgur.com/AC7dgLA.png" alt="" /></div>
      <div className="notifications" id="box">
        <h2>Notifications - <span>2</span></h2>
        <div className="notifications-item"><img src="https://i.imgur.com/uIgDDDd.jpg" alt="img" />
          <div className="text">
            <h4>Samso aliao</h4>
            <p>Samso Nagaro Like your home work</p>
          </div>
        </div>
        <div className="notifications-item"><img src="https://img.icons8.com/flat_round/64/000000/vote-badge.png"
                                                 alt="img" />
          <div className="text">
            <h4>John Silvester</h4>
            <p>+20 vista badge earned</p>
          </div>
        </div>
      </div>
    </nav>
    </>
  )
}
