import React, {useEffect, useState, useRef} from "react";
import ChatInput from "./ChatInput";
import ChatService from "../Service/service";
import { v4 as uuidv4 } from 'uuid';

export default function ChatContainer({ currentChat, currentUser, socket }){
  const [messages, setMessages] = useState([]);
  const [arrivalMessage, setArrivalMessage] = useState(null);
  const scrollRef = useRef();

  useEffect(() =>{
    socket.current.emit("addUser", currentUser._id)
    socket.current.on("getUsers", users =>{
      console.log(users)
    })
  },[currentUser])

  const getValue = async ()=>{
     await ChatService.recieveMessage({
      from: currentUser._id,
      to: currentChat._id,
    })
    .then((res)=>{
      setMessages(res.data)
    })
  }

  useEffect(()=>{
    getValue()
  },[currentChat])

  const handleSendMsg = async (msg) =>{
    // await ChatService.sendMessage({
    //   from: currentUser._id,
    //   to: currentChat._id,
    //   content: msg,
    // })

    // const msgs = [...messages];
    // msgs.push({ fromSelf: true, chat: msg });
    // setMessages(msgs);

    socket.current.emit("send-msg", {
      to: currentChat._id,
      from: currentUser._id,
      msg,
    });
  }

  // useEffect(() =>{
  //   if (socket.current){
  //     socket.current.on("msg-recieve", (msg)=>{
  //       setArrivalMessage({ fromSelf: false, content: msg })
  //     })
  //   }
  // },[])

  // useEffect(() => {
  //   arrivalMessage && setMessages((prev) => [...prev, arrivalMessage]);
  // }, [arrivalMessage]);
  //
  // useEffect(() => {
  //   scrollRef.current?.scrollIntoView({ behavior: "smooth" });
  // }, [messages]);

  return(
    <>
      <div className="chat">
        <div className="chat-header clearfix">
          <div className="row">
            <div className="col-lg-6">
              <a href="" data-toggle="modal" data-target="#view_info">
                <img src="https://bootdey.com/img/Content/avatar/avatar2.png" alt="avatar" />
              </a>
              <div className="chat-about">
                <h6 className="m-b-0">{currentChat.fullName}</h6>
                <small>Last seen: 2 hours ago</small>
              </div>
            </div>
          </div>
        </div>
        <div className="chat-history" style={{ height:"500px" }}>
            {
              messages.map((item)=>(
                <div>
                  <ul className="m-b-0">
                    <li className="clearfix">
                      {/*<div className="message-data text-right">*/}
                      {/*  <span className="message-data-time">10:10 AM, Today</span>*/}
                      {/*  <img src="https://bootdey.com/img/Content/avatar/avatar7.png" alt="avatar" />*/}
                      {/*</div>*/}
                      <div className={`message other-message ${item.fromSelf ? "float-right" : ""}`}>
                        {item.chat}
                      </div>
                    </li>
                  </ul>
                </div>
              ))
            }
        </div>
        <ChatInput handleSendMsg={handleSendMsg} />
      </div>
    </>
  )
}
