import React, {useEffect, useState} from "react";
import PostService from "../../../modules/Post/Service/service";
import {useSelector} from 'react-redux';
import { useNavigate } from "react-router-dom";
import "../../../modules/Post/Styles/createPost.css"
import "../../../modules/Post/Styles/post.css"

export default function EditProfile(){
  let navigate  = useNavigate();
  const [file, setFile] = useState(null);
  const[data, setData] = useState({
    title: "",
    description: "",
  });
  const user = useSelector((state) =>state.profile.value)

  useEffect(()=>{
    // console.log(user)
  },[user])

  const handleChange = (event) =>{
    setData({...data, [event.target.name]: event.target.value});
    setFile(event.target.files[0])
  }

  const submitHandler = async(e)=>{
    e.preventDefault()

    //post
    const newPost = {
      title: data.title,
      authorId: user._id,
      authorName: user.fullName,
      description: data.description,
      image: file
    }

    await PostService.createPost(newPost)

    navigate('/main')
    window.location.reload();
  }

  return(
    <>
      <div className="formMain">
        {/*<Header />*/}
        <div className="container contact-form" style={{ marginTop: "70px" }}>
          <div className="contact-image" >
            <img src="https://image.ibb.co/kUagtU/rocket_contact.png" alt="rocket_contact"/>
          </div>
          <form  onSubmit={submitHandler}>
            <h3>Edit Profile</h3>
            <div className="row justify-content-center" >
              <div className="col-8">
                <div className="form-group">
                  <input type="text" name="fullName" className="form-control" placeholder="FullName *" onChange={handleChange} value={data.title}/>
                </div>
                <div className="form-group">
                  <input id="image" type="file" name="image" className="form-control" accept=".png,.jpeg,.jpg" onChange={handleChange}/>
                </div>
                <div className="form-group">
                  <input id="image" type="file" name="image" className="form-control" accept=".png,.jpeg,.jpg" onChange={handleChange}/>
                </div>
                <div className="form-group">
                  <input id="image" type="file" name="image" className="form-control" accept=".png,.jpeg,.jpg" onChange={handleChange}/>
                </div>
                <div className="form-group">
                  <input id="image" type="file" name="image" className="form-control" accept=".png,.jpeg,.jpg" onChange={handleChange}/>
                </div>
                <div className="form-group">
                  <input type="submit" name="btnSubmit" className="btnContact" value="Send Message"/>
                </div>
              </div>
            </div>
          </form>
        </div>
      </div>
    </>
  )
}
