import React, { useEffect, useState } from 'react';
import ProfileService from "../Service/Service";
import {useSelector} from 'react-redux';


export default function GlobalFriendBtn({classbtn, user, profile}){
  const [friend,setFriend] = useState(false);
  const profileData = useSelector((state) =>state.profile.value)

  useEffect(()=>{
    // if(profile.following.find(item => item._id === user._id)){
    //     setFriend(true)
    // }
    console.log({
      id: profileData.following,
      user: user._id
    })
  },[profileData.following,user._id])

    const addfriend = async () => {
      setFriend(true)
      await ProfileService.addFriend(user._id)
    }

    const removefriend = () =>{
      setFriend(false)
    }

  return(
    <>
      {
            friend ? 
            <button type="button" className="profileinfo-statRight btn btn-primary" >
              UnFriend
            </button>
            :<button type="button" className="profileinfo-statRight btn btn-primary">
              Add Friend
            </button>  
        }
    </>
  ) 
}
