    if (search){
       UserService.searchUser(search)
        .then((res)=>{
          setUsers(res.data.users)
        })
        .catch((err)=>{
          console.log("Khong search")
        })
    }else {

    }
  },[search])

  return(
    <>
      
    </>
  )
}
