import React, { useEffect, useState } from "react";
import {useSelector, useDispatch} from 'react-redux';
import "../Styles/EditProfile.css"
import { useNavigate } from "react-router-dom";
import PostService from "../../../modules/Post/Service/service";

export default function EditProfile(){
  let navigate  = useNavigate();
  const[input, setInput] = useState({
    fullName: "",
    phone: "",
    email: "",
    gender: "",
  });


  const handleChange = (event) =>{
    // setData({...data, [event.target.name]: event.target.value});
    // setFile(event.target.files[0])
  }

  const submitHandle = async(e)=>{
    e.preventDefault()

    //post
    // const newPost = {
    //   title: data.title,
    //   authorId: user._id,
    //   authorName: user.fullName,
    //   description: data.description,
    //   image: file
    // }
    //
    // await PostService.createPost(newPost)
    //
    // navigate('/main')
    // window.location.reload();
  }

  return(
    <div className="formMain">
      {/*<Header />*/}
      <div className="container contact-form" style={{ marginTop: "70px" }}>
        <br/>
        <button type="button" className="btn btn-primary btn-rounded" onClick={() => { navigate(-1) }}>Go Back</button>
        <div className="contact-image" >
          <img src="https://image.ibb.co/kUagtU/rocket_contact.png" alt="rocket_contact"/>
        </div>
        <form  onSubmit={submitHandle}>
          <h3>Drop Us a Message</h3>
          <div className="row">
            <div className="col align-self-center">
              <div className="form-group">
                <input type="text" name="title" className="form-control" placeholder="Full Name *" onChange={handleChange}/>
              </div>
              <div className="form-group">
                <input type="text" name="title" className="form-control" placeholder="Phone *" onChange={handleChange}/>
              </div>
              <div className="form-group">
                <input type="text" name="title" className="form-control" placeholder="Email *" onChange={handleChange}/>
              </div>
              <div className="form-group">
                <input type="text" name="title" className="form-control" placeholder="Gender *" onChange={handleChange}/>
              </div>
              <div className="form-group">
                <input type="submit" name="btnSubmit" className="btnContact" value="Send Message"/>
              </div>
            </div>
          </div>
        </form>
      </div>
    </div>
  )
}
