import React, {useState, useEffect } from "react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  ArcElement ,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import { Pie } from "react-chartjs-2";

ChartJS.register(
  CategoryScale,
  LinearScale,
  ArcElement ,
  Title,
  Tooltip,
  Legend
);

export default function PieChart(){
  const [chartData, setChartData] = useState({
    datasets: [],
  });

  const [chartOptions, setChartOptions] = useState({});

  useEffect(() => {
    setChartData({
      labels: ["John", "Kevin", "Geroge", "Micheal", "Oreo"],
      datasets: [
        {
          label: "Xanh",
          data: [12, 55, 34, 120, 720],
          borderColor: "rgb(53, 162, 235)",
          backgroundColor: [
            "rgba(53, 162, 235, 0.4)",
            "rgb(252, 8, 110)",
            "rgb(86, 8, 110)",
            ""
          ],
        },
        // {
        //   label: "Do ",
        //   data: [2, 50, 30, 100, 600],
        //   borderColor: "rgb(53, 162, 235)",
        //   backgroundColor: "red",
        // },
      ],
    });
    setChartOptions({
      responsive: true,
      plugins: {
        legend: {
          position: "right",
        },
        title: {
          display: true,
          text: "Whom'st let the dogs out",
        },
      },
    });
  }, []);


  return(
    <>
      <Pie options={chartOptions} data={chartData} />
    </>
  )
}
